def get_profile_image_url(twitter_handle):
	try:
		user_info = twitter_api.get_user(screen_name=twitter_handle)
		img_url_normal = user_info.profile_image_url
		img_url_src = img_url_normal.replace("normal", "200x200")
	except tweepy.error.TweepError:
		img_url_src = ""

	return img_url_src

    
